vim.cmd[[highlight clear]]
vim.g.colors_name = 'turnip-light'
-- Colorscheme generated by https://github.com/arcticlimer/djanho

local highlight = function(group, bg, fg, attr)
    fg = fg and 'guifg=' .. fg or ''
    bg = bg and 'guibg=' .. bg or ''
    attr = attr and 'gui=' .. attr or ''

    vim.api.nvim_command('highlight ' .. group .. ' '.. fg .. ' ' .. bg .. ' '.. attr)
end

local link = function(target, group)
    vim.api.nvim_command('highlight! link ' .. target .. ' '.. group)
end

local Color0 = '#aaaaaa'
local Color2 = '#487d76'
local Color4 = '#664e4d'
local Color1 = '#e8da5e'
local Color5 = '#cf433e'
local Color6 = '#ffffff'
local Color7 = '#444444'
local Color9 = '#cccccc'
local Color3 = '#92b55f'
local Color8 = '#dae5e3'
local Color10 = '#f5f5f5'

highlight('Comment', nil, Color0, nil)
highlight('String', nil, Color1, nil)
highlight('Number', nil, Color1, nil)
highlight('TSCharacter', nil, Color2, nil)
highlight('Keyword', nil, Color3, nil)
highlight('Type', nil, Color2, nil)
highlight('Function', nil, Color1, nil)
highlight('Error', Color4, Color5, nil)
highlight('StatusLine', Color6, Color3, nil)
highlight('WildMenu', Color6, Color7, nil)
highlight('Pmenu', Color6, Color7, nil)
highlight('PmenuSel', Color7, nil, nil)
highlight('PmenuThumb', Color6, Color7, nil)
highlight('Normal', Color6, Color7, nil)
highlight('Visual', Color8, nil, nil)
highlight('CursorLine', Color8, nil, nil)
highlight('ColorColumn', Color8, nil, nil)
highlight('SignColumn', Color6, nil, nil)
highlight('LineNr', nil, Color9, nil)
highlight('TabLine', Color10, Color0, nil)
